import React, { Component } from "react";
import '../styles/hub.css'

class Hub extends Component {
    constructor() {
        super();
        this.state = {
            slide: 1,
            max: 3
        };
        this.nextSlide = this.nextSlide.bind(this);
        this.prevSlide = this.prevSlide.bind(this);
    }

    nextSlide = () => (this.state.slide === this.state.max) ? this.setState({ slide: 1 }) : this.setState({ slide: this.state.slide + 1 });

    prevSlide = () => (this.state.slide === 1) ? this.setState({ slide: this.state.max }) : this.setState({ slide: this.state.slide - 1 });

    carouselControls() {
        return (
            <div class="carousel-controls">
                <div onClick={this.prevSlide} class="prev-slide">
                    <span>&lsaquo;</span>
                </div>
                <div onClick={this.nextSlide} class="next-slide">
                    <span>&rsaquo;</span>
                </div>
            </div>
        );
    }

    render() {
        const { slide } = this.state;
        return (
            <div>
                <div class="carousel">
                    <ul class="slides">
                        <li className={(slide === 1) ? 'selected' : 'not'}>
                            <div class="slide-image">
                                <img src="https://upload.wikimedia.org/wikipedia/commons/9/9e/Timisoara_-_Regional_Business_Centre.jpg" />
                            </div>
                            {this.carouselControls()}
                        </li>
                        <li className={(slide === 2) ? 'selected' : 'not'}>
                            <div class="slide-image">
                                <img src="https://content.r9cdn.net/rimg/dimg/db/02/06b291e8-city-14912-171317ad83a.jpg?width=1750&height=1000&xhint=3040&yhint=2553&crop=true" />
                            </div>
                            <div class="carousel-controls">
                                <div onClick={this.prevSlide} class="prev-slide">
                                    <span>&lsaquo;</span>
                                </div>
                                <div onClick={this.nextSlide} class="next-slide">
                                    <span>&rsaquo;</span>
                                </div>
                            </div>
                        </li>
                        <li className={(slide === 3) ? 'selected' : 'not'}>
                            <div class="slide-image">
                                <img src="https://speakzeasy.files.wordpress.com/2015/05/twa_blogpic_timisoara-4415.jpg" />
                            </div>
                            <div class="carousel-controls">
                                <div onClick={this.prevSlide} class="prev-slide">
                                    <span>&lsaquo;</span>
                                </div>
                                <div onClick={this.nextSlide} class="next-slide">
                                    <span>&rsaquo;</span>
                                </div>
                            </div>
                        </li>
                    </ul>
                </div>
            </div>
        );
    }
}

export default Hub;
