import React, { Component } from "react";
import '../styles/hub.css'

class Hub extends Component {
    constructor() {
        super();
        this.state = {
            slide: 1,
            max: 3
        };
        this.nextSlide = this.nextSlide.bind(this);
    }

    nextSlide() {
        const { slide, max } = this.state;
        if (slide === max) this.setState({ slide: 1 });
        else this.setState({ slide: slide + 1 });
    }

    render() {
        // const { slide } = this.state;
        return (
            <div>
  <div class="carousel">
    <ul class="slides">
      <input type="radio" name="radio-buttons" id="img-1" checked />
      <li class="slide-container">
        <div class="slide-image">
          <img src="https://upload.wikimedia.org/wikipedia/commons/9/9e/Timisoara_-_Regional_Business_Centre.jpg"/>
        </div>
        <div class="carousel-controls">
          <div for="img-3" class="prev-slide">
            <span>&lsaquo;</span>
          </div>
          <div for="img-2" class="next-slide">
            <span>&rsaquo;</span>
          </div>
        </div>
      </li>
      <input type="radio" name="radio-buttons" id="img-2" />
      <li class="slide-container">
        <div class="slide-image">
          <img src="https://content.r9cdn.net/rimg/dimg/db/02/06b291e8-city-14912-171317ad83a.jpg?width=1750&height=1000&xhint=3040&yhint=2553&crop=true"/>
        </div>
        <div class="carousel-controls">
          <div for="img-1" class="prev-slide">
            <span>&lsaquo;</span>
          </div>
          <div for="img-3" class="next-slide">
            <span>&rsaquo;</span>
          </div>
        </div>
      </li>
      <input type="radio" name="radio-buttons" id="img-3" />
      <li class="slide-container">
        <div class="slide-image">
          <img src="https://speakzeasy.files.wordpress.com/2015/05/twa_blogpic_timisoara-4415.jpg"/>
        </div>
        <div class="carousel-controls">
          <div for="img-2" class="prev-slide">
            <span>&lsaquo;</span>
          </div>
          <div for="img-1" class="next-slide">
            <span>&rsaquo;</span>
          </div>
        </div>
      </li>
    </ul>
  </div>
</div>
        );
    }
}

export default Hub;
