import React, { Component } from "react";
import { Link } from 'react-router-dom'
import "../styles/header.css"

class Header extends Component {
    constructor() {
        super();
        this.state = {
            menu: false,
            test: 1
        };
        this.toggleMenu = this.toggleMenu.bind(this);
        this.setTest = this.setTest.bind(this);
    }

    toggleMenu = () => {
        const prev = this.state.menu;
        this.setState({ menu: !prev });
    };

    setTest = () => {
        const prev = this.state.test;
        this.setState({ test: prev + 1 });
    }

    render() {
        const { menu, test } = this.state
        return (
            <div className="NavBar">
                <svg><path d="M41,14H7a2,2,0,0,1,0-4H41A2,2,0,0,1,41,14Z" fill="#004aa2" /><path d="M41,26H7a2,2,0,0,1,0-4H41A2,2,0,0,1,41,26Z" fill="#004aa2" /><path d="M41,38H7a2,2,0,0,1,0-4H41A2,2,0,0,1,41,38Z" fill="#004aa2" /></svg>
                <img src="logo.png" alt="logo" id="logo" />
                {/* <p onClick={ this.toggleMenu }>{ menu ? 1 : 0 }</p> */}
                {menu && <p onClick={ this.setTest }>{ test }</p>}
                <Link to='login'><img src="login-removebg-preview.png" alt="login" id="login" /></Link>
            </div>
        )
    }
}

export default Header;
