import React, { Component } from "react";
import { Link } from 'react-router-dom'
import "../styles/header.css"

class Header extends Component {
    constructor() {
        super();
        this.state = {
            menu: false,
            itens: ['Minha Conta', 'Quem Somos', 'Áreas de Atuação', 'Ações em Destaque', 'Nossos Parceiros', 'Depoimentos', 'Blog', 'Fale Conosco', 'Contato']
        };
        this.toggleMenu = this.toggleMenu.bind(this);
    }

    toggleMenu = () => {
        this.setState({ menu: !this.state.menu });

    }

    hamburguerMenu() {
        const { itens, menu } = this.state;
        return (
            <div className={menu ? 'login' : 'logo'}>
                { itens.map(item => <div key={item} className='menu-items'>{item}</div>) }
            </div>
        )
    }

    render() {
        return (
            <div>
                {this.hamburguerMenu()}
                <div className="NavBar">
                    {/* <svg id="hamb" onClick={this.toggleMenu}><path d="M41,14H7a2,2,0,0,1,0-4H41A2,2,0,0,1,41,14Z" fill="#004aa2" /><path d="M41,26H7a2,2,0,0,1,0-4H41A2,2,0,0,1,41,26Z" fill="#004aa2" /><path d="M41,38H7a2,2,0,0,1,0-4H41A2,2,0,0,1,41,38Z" fill="#004aa2" /></svg> */}
                    <div class="hamburger-lines">
                        <span class="line line1"></span>
                        <span class="line line2"></span>
                        <span class="line line3"></span>
                    </div>
                    <img src="logo.png" alt="logo" id="logo" />
                    <Link to='login'><img src="login-removebg-preview.png" alt="login" id="login" /></Link>
                </div>
            </div>
        )
    }
}

export default Header;
